<?phpnamespace plugins\riMeta;use Symfony\Component\HttpFoundation\Request;use plugins\riSimplex\Controller;use plugins\riPlugin\Plugin;use Symfony\Component\HttpFoundation\Response;class AdminController extends Controller{    public function metaListAction(Request $request){                $metas = \plugins\riPlugin\Plugin::get('riMeta.Metas')->findByObjectsId($request->get("objects_id"), $request->get('objects_type'));        $list = list_meta();               $this->container->get('templating.holder')->add('main', $this->view->render('riMeta::list', array('plugins' => $plugins,'metas' => $metas->get(),'objects_id' => $request->get("objects_id"),'objects_type' =>$request->get('objects_type'),'list' => $list)));        //var_dump($riview);die("ss");        return $this->render('riMeta::admin_layout');      }    public function metaAction(Request $request){                global $db;        $data['objects_id'] = $request->get("objects_id");        $data['objects_type'] = $request->get("objects_type");        $data['metas_value'] = $request->get("meta_value");        $data['metas_key'] = ($request->get("meta_name") != null)?$request->get("meta_name"):$request->get("meta_name_dropdown");        $action = $request->get("action");        switch ($action){            case "delete":                $db->Execute("DELETE FROM " . TABLE_METAS . " WHERE id=" .$request->get("id"));                break;            case "insert":{                    zen_db_perform(TABLE_METAS, $data);                    break;            }            case "update":{                    zen_db_perform(TABLE_METAS, $data ,"update","id = " .$request->get("id"));                    break;            }        }        $metas = \plugins\riPlugin\Plugin::get('riMeta.Metas')->findByObjectsId($request->get("objects_id"), $request->get('objects_type'));        $list = list_meta();        return $this->render('riMeta::list', array('list' => $list,'metas' => $metas->get(),'objects_id' => $request->get("objects_id"),'objects_type' =>$request->get('objects_type')));        //var_dump($riview);die("ss");        //return $this->render('riMeta::admin_layout');      }       }